mapper.select:
SELECT * FROM pedometer
WHERE p_id = #{userId}
AND p_date BETWEEN #{startDate,jdbcType=DATE} AND #{endDate,jdbcType=DATE}

mapper.update:
   	UPDATE pedometer
	SET p_stepgoal = #{pStepGoal}
	WHERE p_id = #{pId}
  	AND TRUNC(p_date) = TRUNC(#{pDate})

UPDATE pedometer
SET p_stepcnt = #{pStepCnt}
WHERE p_id = #{pId}
AND TRUNC(p_date) = TRUNC(#{pDate})
